import style from "./AppButton.module.scss";
import React from "react";
export const AppButton = ({ children, padding, color } : { children?: string, padding?:string, color?:string }) => {
  const refBtn = React.useRef<any>();
  React.useEffect(() => {
    refBtn.current.style.background = color;
    refBtn.current.style.padding = `${padding}px `;
  },[]);
  return <button ref={refBtn} className={style.app_btn}>{children}</button>;
};
