import style from "./ModalBurger.module.scss";
import React, { HtmlHTMLAttributes } from "react";
import clsx from "clsx";
export const ModalBurger = ({ items, img, text }: { items?:Array<any>, img?:string, text?: string}) => {
  const [ toggle, setToggle ] = React.useState(false);
  const burgerRef = React.useRef<<HTMLDivElement>(null);
  const handleToggle = () => {
    setToggle(!toggle);
    setTimeout(() => document.body.addEventListener("click", handleOutSideClick), 500);
  };
  const handleOutSideClick = (e:any) => {
    const path = e.path || (e.composedPath && e.composedPath());
    if (!path.includes(burgerRef.current)) {
      setToggle(false);
    };
    document.body.removeEventListener("click", handleOutSideClick);
  };
 
  
  return <>
    <img src={img} alt={text} />
    <div onClick={handleToggle} className={style.top_nav_text}>{text}</div>
    <div className={style.modal_img}>
      <img 
        onClick={handleToggle} className={clsx(style.img,{[style.img_active]:toggle})} src="./assets/v.png" alt="" />
      <div ref={burgerRef} className={clsx(style.modal, {[style.modal_active]:toggle})}>
        {toggle? <>{items?.map(item => <div>{item}123</div>)}</> : null}
      </div>
    </div></>;
};
